<?php/*|===========================================================================||	MODELO DE DADOS PARA A TABELA           						        ||===========================================================================||																			||	VERSO:								1.0.0								||	 																		|	|===========================================================================|*/	Class clientesModel extends Model {		public $_tabela = "clientes";	//	ATRIBUTO PARA SETAR QUAL A TABELA QUE EST EM USO			# ---------------------------------------------------------------------------------------------------------------------------------	# METODO 001: [ PUBLIC FUNCTION LISTA REGISTROS ]	# ESTE METODO  RETORNA TODOS OS REGISTROS DO BANCO DE DADOS	# ---------------------------------------------------------------------------------------------------------------------------------				public function listaClientes($condicao = null, $qtd = null, $offset = null) {			return $this->read($condicao, $qtd, $offset, 'id ASC');		}			# ---------------------------------------------------------------------------------------------------------------------------------	# METODO 002: [ PUBLIC FUNCTION MOSTRA REGISTRO ]	# ESTE METODO RETORNA UM NICO REGISTRO	# ---------------------------------------------------------------------------------------------------------------------------------						public function clienteShow($where) {			return $this->read($where, null, null, 'id ASC');		}	}?>